<svelte:options runes={true} />
<script lang="ts">
	let { moodRating = 1, colored = false }: { moodRating?: number, colored?: boolean } = $props();
	let rating = $derived(Math.max(1, Math.min(5, moodRating)));
	let color = $derived.by(() => {
		if (colored) {
			switch (rating) {
				case 1:
					return "var(--awful)";
				case 2:
					return "var(--meh)";
				case 3:
					return "var(--balanced)";
				case 4:
					return "var(--energized)";
				case 5:
					return "var(--ecstatic)";
				default:
					return "#fff";
			}
		} else {
			return "#fff";
		}
	});
</script>

